import React, { Component } from "react";
import strings from "../localization";
import { isUserLoggedIn, logout } from "../base/OAuth";
import { Link } from "react-router-dom";
import LanguageSwitcher from "./LanguageSwitcher";
import { bindActionCreators } from "redux";
import { withRouter } from "react-router-dom";
import connect from "react-redux/es/connect/connect";
import * as Actions from "../actions/Actions";

class Header extends Component {
  logout() {
    logout();
    this.props.logout(this.props.user);
  }

  render() {
    return (
      <div id="header">
        <div className="brand">
          <div className="logo">
            <Link to={"/"}>
              <img src={"images/logo.png"} />
            </Link>
          </div>
          <div className="header-brand-title">{strings.menu.Home}</div>
        </div>
        <div className="menu">
          {!isUserLoggedIn() && (
            <React.Fragment>
              <div className="btn-login">
                <Link to={"/login"}>{strings.menu.Login}</Link>
              </div>
              <div className="btn-register">
                <Link to={"/registration"}>{strings.menu.Register}</Link>
              </div>
            </React.Fragment>
          )}
          {isUserLoggedIn() && (
            <React.Fragment>
              <div className="btn-login">
                <Link to={"/profile"}>{strings.menu.Profile}</Link>
              </div>
              <div className="btn-register" onClick={() => this.logout()}>
                <a>{strings.menu.Logout}</a>
              </div>
            </React.Fragment>
          )}

          <LanguageSwitcher />
        </div>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators(
    {
      logout: Actions.logout
    },
    dispatch
  );
}

function mapStateToProps({ authReducers, siteDataReducers }) {
  return {
    user: authReducers.user,
    language: siteDataReducers.language
  };
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Header));
