import React from "react";
import Home from "./pages/Home";
import Error from "./pages/Error";
import Forbidden from "./pages/Forbidden";
import NotFound from "./pages/NotFound";
import Login from "./pages/user/Login";
import RegistrationFirstStep from "./pages/user/RegistrationFirstStep";
import RegistrationSecondStep from "./pages/user/RegistrationSecondStep";
import Blogs from "./pages/Blogs";
import BlogDetails from "./pages/BlogDetails";
import UserDetails from "./pages/UserDetails";
import SearchResults from "./pages/SearchResults";

import { Route } from "react-router-dom";
import { isUserLoggedIn } from "./base/OAuth";

let ROUTES = {
  Home: {
    path: "/",
    component: <Home />,
    auth: false,
  },
  Error: {
    path: "/error",
    component: <Error />,
    auth: false,
  },
  Forbidden: {
    path: "/forbidden",
    component: <Forbidden />,
    auth: false,
  },
  NotFound: {
    path: "/not-found",
    component: <NotFound />,
    auth: false,
  },
  Login: {
    path: "/login",
    component: <Login />,
    auth: false,
  },
  Registration: {
    path: "/registration",
    component: <RegistrationFirstStep />,
    auth: false,
  },
  Registration2: {
    path: "/registration2",
    component: <RegistrationSecondStep />,
    auth: false,
  },
  Blogs: {
    path: "/blogs",
    component: <Blogs />,
    auth: false,
  },
  BlogDetails: {
    path: "/blog",
    component: <BlogDetails />,
    auth: false,
  },
  SearchResults: {
    path: "/search",
    component: <SearchResults />,
    auth: false,
  },
  UserDetails: {
    path: "/user",
    component: <UserDetails />,
    auth: false,
  },
};

export default ROUTES;

function getRoute(path) {
  for (const [key, value] of Object.entries(ROUTES)) {
    if (value.path === path) {
      return value;
    }
  }

  return null;
}

export function checkPath(path) {
  let pathObject = getRoute(path);

  if (!pathObject) {
    return true;
  }

  if (pathObject.auth) {
    return !isUserLoggedIn();
  }

  return false;
}

export function getRoutes() {
  let result = [];

  for (const [key, value] of Object.entries(ROUTES)) {
    result.push(
      <Route
        key={"route-" + result.length}
        exact
        path={value.path}
        render={() => value.component}
      />
    );
  }

  return result;
}
