import React from "react";
import Page from "../../common/Page";
import { bindActionCreators } from "redux";
import * as Actions from "../../actions/Actions";
import { withRouter } from "react-router-dom";
import connect from "react-redux/es/connect/connect";
import strings from "../../localization";
import UserInfo from "../../components/UserInfo";
import SocialShare from "../../components/SocialShare";
import {
  changeUserState,
  getUserRegistrationDetailsData,
} from "../../services/UserService";
import UserState from "../../constants/UserState";

class RegistrationSecondStep extends Page {
  registerValidationList = {
    email: [{ type: Validators.EMAIL }],
    password: [{ type: Validators.REQUIRED }],
    repeatPassword: [{ type: Validators.REQUIRED }],
    firstName: [{ type: Validators.REQUIRED }],
    lastName: [{ type: Validators.REQUIRED }],
    weight: [{ type: Validators.IS_NUMBER }],
    height: [{ type: Validators.IS_NUMBER }],
    position: [{ type: Validators.REQUIRED }],
    gender: [{ type: Validators.REQUIRED }],
    dateOfBirth: [{ type: Validators.REQUIRED }],
    countryId: [{ type: Validators.REQUIRED }],
    profileImage: [{ type: Validators.REQUIRED }],
  };

  componentDidMount() {
    this.props.showLoader();

    getUserRegistrationDetailsData().then((response) => {
      this.props.hideLoader();

      if (!response || !response.ok) {
        return;
      }

      this.setState({
        playerInfo: response.data,
      });
    });
  }

  goHome() {
    this.props.history.push("/");
  }

  goBack() {
    this.props.history.push("/edit-profile-details");
  }

  changeState(state) {
    changeUserState({
      state:
        this.state.playerInfo.user.state == UserState.Active
          ? UserState.Inactive
          : UserState.Active,
    }).then((response) => {
      if (!response || !response.ok) {
        return;
      }

      let playerInfo = this.state.playerInfo;

      playerInfo.user.state = response.data.state;

      this.setState({
        playerInfo: playerInfo,
        showConfirm: false,
      });
    });
  }

  toggleConfirm(showConfirm) {
    this.setState({
      showConfirm: showConfirm,
    });
  }

  render() {
    if (!this.state.playerInfo) {
      return "";
    }

    return (
      <div id="registration-final-step-page" className="page-container">
        {this.props.showSteps && (
          <div className="registration-step-container">
            <RegistrationStep step={3} />
          </div>
        )}

        <div className="user-info-container">
          <UserInfo
            playerInfo={this.state.playerInfo}
            showEdit={this.props.showEdit}
          />

          <div className="share">
            <SocialShare
              url={
                window.location.href + "?user=" + this.state.playerInfo.user.id
              }
            />
          </div>

          <div className="status-container">
            {this.state.showConfirm && (
              <div className="confirm-container">
                <p>{strings.userInfo.confirmAction}</p>
                <div className="controls">
                  <span onClick={() => this.changeState()}>
                    {strings.userInfo.yes}
                  </span>
                  <span onClick={() => this.toggleConfirm(false)}>
                    {strings.userInfo.no}
                  </span>
                </div>
              </div>
            )}

            {!this.state.showConfirm &&
              this.props.showEdit &&
              this.state.playerInfo.user.state == UserState.Active && (
                <span onClick={() => this.toggleConfirm(true)}>
                  {strings.userInfo.deactivate}
                </span>
              )}

            {!this.state.showConfirm &&
              this.props.showEdit &&
              this.state.playerInfo.user.state == UserState.Inactive && (
                <span onClick={() => this.toggleConfirm(true)}>
                  {strings.userInfo.activate}
                </span>
              )}
          </div>
        </div>

        {this.props.showSteps && (
          <div className="navigation-container">
            <div className="navigation">
              <div className="navigation-element" onClick={() => this.goBack()}>
                <img
                  className="regular-arrow"
                  src={"images/arrows/arrow-left.png"}
                />
                <img
                  className="disabled-arrow"
                  src={"images/arrows/arrow-left-disabled.png"}
                />
                <span>{strings.back}</span>
              </div>
              <div className="navigation-element" onClick={() => this.goHome()}>
                <span>{strings.goToHomePage}</span>
                <img
                  className="regular-arrow"
                  src={"images/arrows/arrow-right.png"}
                />
                <img
                  className="disabled-arrow"
                  src={"images/arrows/arrow-right-disabled.png"}
                />
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators(
    {
      showLoader: Actions.showLoader,
      hideLoader: Actions.hideLoader,
    },
    dispatch
  );
}

function mapStateToProps({ siteDataReducers }) {
  return siteDataReducers;
}

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(RegistrationSecondStep)
);
